import React, {useState} from "react";
import { Link } from "react-router-dom";
import { Button, Form, Message } from "semantic-ui-react";
import axios from 'axios';
import Layout from "./Layout";
import history from "../../utils/history"
import { setUserSession } from '../../utils/Common';
// TODO: Add more fields like First/Last name, in greek
// send request to db to verify
// error message if something is wrong
export default function Register() {
  const [loading, setLoading] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  // handle button click of login form
  const handleLogin = () => {
    setError(null);
    setLoading(true);
    axios.post('http://localhost:3001/api/login', { email: email, password: password }).then(response => {
      setLoading(false);
      console.log("response = ", response)
      console.log("response.data.user = ",response.data.user )

      setUserSession(response.data.token, response.data.user);
      // when the user logs in goes to /Home, 
      // TODO: Try this again
      // history.push('/');

    }).catch(error => {
      setLoading(false);
      if (error.response.status === 401) setError(error.response.data.message);
      else setError("Something went wrong. Please try again later.");
    });
  }
    return (
      <Layout header="Εγγραφείτε εδώ">
        <Form loading={loading}>
        <Form.Input
            fluid
            icon="user"
            iconPosition="left"
            placeholder="Full Name"
            className="auth-input-field"
            onChange={e => setFirstName(e.target.value)}
          />
          <Form.Input
            fluid
            icon="user"
            iconPosition="left"
            placeholder="Full Name"
            className="auth-input-field"
            onChange={e => setLastName(e.target.value)}
          />
          <Form.Input
            fluid
            icon="mail"
            iconPosition="left"
            placeholder="E-mail address"
            className="auth-input-field"
            onChange={e => setEmail(e.target.value)}
          />
          <Form.Input
            fluid
            icon="lock"
            iconPosition="left"
            placeholder="Password"
            type="password"
            className="auth-input-field"
            onChange={e => setPassword(e.target.value)}
          />
          <Form.Input
            fluid
            icon="lock"
            iconPosition="left"
            placeholder="Confirm Password"
            type="password"
            className="auth-input-field"
            onChange={e => setConfPassword(e.target.value)}
          />
          <Button color="teal" fluid size="huge" onClick={handleLogin} disabled={loading}>
            <Link to="/">Εγγραφή</Link>
          </Button>
        </Form>
          <Message size="big">
            <Link to="/login">Already Registered?</Link>
          </Message>
        </Layout>
      );
}
