import React from 'react'
import { Accordion, Segment, Grid, Header } from 'semantic-ui-react'
import './index.css'



const Level1Content = (
  <div>
    Welcome to level 1
  </div>
)
const Level2Content = (
  <div>
    Welcome to level 2
  </div>
)
const Level3Content = (
  <div>
    Welcome to level 3
  </div>
)

const rootPanels = [
  { key: 'panel-1', title: 'Level 1', content: { content: Level1Content } },
  { key: 'panel-2', title: 'Level 2', content: { content: Level2Content } },
  { key: 'panel-3', title: 'Level 3', content: { content: Level3Content } },
]

export default function Faq() {
    return (
        
        <Segment style={{ padding: '6em 4em' }} vertical>
          <Header>Assa</Header>
            <Grid centered className="align-text">
                <Accordion defaultActiveIndex={0} panels={rootPanels} styled />
            </Grid>
        </Segment>
    )
}
